import React, { useState } from "react";
import Container from "../../Container/Container";
import ExteriorSelectionComponent from "./ExteriorSelectionComponent";
import "./ExteriorContent.css";
import Button from "./../../Button/Button";

const DISPLAYED_CONFIGS = 6;

const ExteriorInnerContent = ({
  displayedSide,
  clickEvent,
  data,
  type,
  updateConfiguration,
  size,
}) => {
  const [prevIndex, setPrevIndex] = useState(0);
  const [displayedData, setDisplayedData] = useState(
    data.slice(prevIndex, DISPLAYED_CONFIGS)
  );
  console.log("displayedData", displayedData);

  const setDisplayedItems = (prevOrNext) => {
    if (prevOrNext === "prev") {
      if (prevIndex === 0) {
        return;
      } else {
        // const newPrevIndex = prevIndex - DISPLAYED_CONFIGS;
        setPrevIndex(prevIndex - DISPLAYED_CONFIGS);
        setDisplayedData(data.slice(prevIndex, DISPLAYED_CONFIGS));
      }
    } else {
      if (prevIndex + DISPLAYED_CONFIGS > data.length) {
        return;
      } else {
        setPrevIndex(prevIndex + DISPLAYED_CONFIGS);
        setDisplayedData(data.slice(prevIndex, DISPLAYED_CONFIGS));
      }
    }
  };
  return (
    <Container>
      <Container classes="SelectionComponentContainer">
        {data.map((option) => {
          return (
            <ExteriorSelectionComponent
              key={option.id}
              value={400}
              clickEvent={clickEvent}
              label={option.image}
              type={type}
              multiple={false}
              updateConfiguration={updateConfiguration}
              size={size}
            />
          );
        })}
      </Container>
      <Container classes="ExteriorInnerContentPaginationContainer">
        <Button
          onClick={() => setDisplayedItems("prev")}
          value="prev"
          classes="ExteriorInnerContentPaginationButton"
          buttonText={"<"}
        />
        <Button
          onClick={() => setDisplayedItems("next")}
          value="next"
          classes="ExteriorInnerContentPaginationButton"
          buttonText={">"}
        />
      </Container>
    </Container>
  );
};

export default ExteriorInnerContent;
